import {Box, Button, Input, Title} from "../ui";
import {useEffect, useState} from "react";
import EmailValidator from "../validators/email-validator";
import PasswordValidator from "../validators/password-validator";
import {useDispatch, useSelector} from "react-redux";
import {useNavigate} from "react-router-dom";
import {loginAction} from "../store/userReducer";

const Login = () => {

    const dispatch = useDispatch()
    const navigate = useNavigate()

    const isAuth = useSelector(state => state.user.isAuth)

    const localEmail = window.localStorage.getItem('Login_email') ? window.localStorage.getItem('Login_email') : ""
    const localPass = window.localStorage.getItem('Login_pass')

    console.log(localEmail)

    useEffect(() =>{
        if (isAuth){
            navigate("/changePass")
        }
    }, [isAuth])

    const [email, setEmail] = useState({
        value: "",
        error: ""
    })

    const [pass, setPass] = useState({
        value: "",
        error: ""
    })

    const handleClick = () => {
        setPass({...pass, error: ""})
        setEmail({...email, error: ""})
        if (!EmailValidator(email, setEmail)){
            return false
        }
        if (!PasswordValidator(pass, setPass)){
            return false
        }
        dispatch(loginAction())
    }

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            <Input value={email.value} onChange={(e) => setEmail({error: "", value: e.target.value})} name="email" type="email"
                   placeholder="Email"/>
            <Input value={pass.value} onChange={(e) => setPass({error: "", value: e.target.value})} type="password" placeholder="Password"/>
            {email.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{email.error}</div>}
            {pass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{pass.error}</div>}
            <Button onClick={handleClick}>
                Войти
            </Button>
        </Box>
    )
}

export default Login;
