import {Box, Button, Input, Title} from "../ui";
import {useEffect, useState} from "react";

const useValidation = (value, validations) => {
    const [isEmpty, setEmpty] = useState(true)

    useEffect(() => {
        for (const validation in validations) {
            switch (validation) {
                case "minLength":
                    break;
                case "isEmpty":
                    value ? setEmpty(false) : setEmpty(true)
                    break;
            }
        }
    }, [value])
}

const useInput = (initialValue) => {
    const [value, setValue] = useState(initialValue)
    const [isDirty, setDirty] = useState(false)

    const onChange = (e) => {
        setValue(e.target.value)
    }

    const onBlur = (e) => {
        setDirty(true)
    }

    return {
        value, onChange, onBlur
    }
}

const Login = () => {
    const email = useInput("")
    const password = useInput("")

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            <form>
                <Input onChange={(e) => email.onChange(e)} onBlur={(e) => email.onBlur(e)} value={email.value}
                       name="email" type="email" placeholder="Email"/>
                <Input onChange={(e) => password.onChange(e)} onBlur={(e) => password.onBlur(e)} value={password.value}
                       type="password" placeholder="Password"/>
                <Button type="submit">
                    Войти
                </Button>
            </form>
        </Box>
    )
}

export default Login
