import styled from "styled-components";

export const Box = styled.div`
  padding: 20px;
  border-radius: 20px;
  background-color: lightskyblue;
  min-width: 400px;
  text-align: center;
`;

export const Input = styled.input`
  border-radius: 10px;
  padding: 5px 10px;
  width: calc(100% - 20px);
  margin-bottom: 20px;
`;

export const Title = styled.div`
  font-size: 40px;
  font-weight: 600;
  margin-bottom: 30px;
`;

export const Button = styled.button`
  background-color: orange;
  font-size: 20px;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  padding: 10px 15px;
`;
