import {Box, Button, Input, Title} from "../ui";
import {useEffect, useState} from "react";
import EmailValidator from "../validators/email-validator";
import PasswordValidator from "../validators/password-validator";
import {useDispatch, useSelector} from "react-redux";
import {useNavigate} from "react-router-dom";
import {registrationAction} from "../store/userReducer";

const Registration = () => {

    const dispatch = useDispatch()
    const navigate = useNavigate()

    const isAuth = useSelector(state => state.user.isAuth)

    useEffect(() =>{
        if (isAuth){
            navigate("/changePass")
        }
    }, [isAuth])

    const [email, setEmail] = useState({
        value: "",
        error: ""
    })

    const [pass, setPass] = useState({
        value: "",
        error: ""
    })

    const [repeatPass, setRepeatPass] = useState({
        value: "",
        error: ""
    })

    const handleClick = () => {
        setPass({...pass, error: ""})
        setEmail({...email, error: ""})
        setRepeatPass({...repeatPass, error: ""})
        if (!EmailValidator(email, setEmail)){
            return false
        }
        if (!PasswordValidator(pass, setPass)){
            return false
        }
        if (repeatPass.value !== pass.value){
            setRepeatPass({...repeatPass, error: "Пароли должны совпадать"})
            return false
        }
        dispatch(registrationAction())
    }

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Регистрация
            </Title>
            <Input value={email.value} onChange={(e) => setEmail({error: "", value: e.target.value})} name={"email"} type={"email"} placeholder={"Email"}/>
            <Input value={pass.value} onChange={(e) => setPass({error: "", value: e.target.value})} type="password" placeholder={"Password"}/>
            <Input value={repeatPass.value} onChange={(e) => setRepeatPass({error: "", value: e.target.value})} type="password" placeholder={"Repeat password"}/>
            {email.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{email.error}</div>}
            {pass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{pass.error}</div>}
            {repeatPass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{repeatPass.error}</div>}
            <Button onClick={handleClick}>
                Зарегистрироваться
            </Button>
        </Box>
    )
}

export default Registration
