import {Box, Button, Input, Title} from "../ui";
import {useEffect, useState} from "react";
import PasswordValidator from "../validators/password-validator";
import {useSelector} from "react-redux";

const ChangePassword = () => {

    const isAuth = useSelector(state => state.user.isAuth)

    useEffect(() =>{
        if (!isAuth){
            navigate("/")
        }
    }, [isAuth])

    const [oldPass, setOldPass] = useState({
        value: "",
        error: ""
    })

    const [pass, setPass] = useState({
        value: "",
        error: ""
    })

    const [repeatPass, setRepeatPass] = useState({
        value: "",
        error: ""
    })

    const handleClick = () => {
        setPass({...pass, error: ""})
        setRepeatPass({...repeatPass, error: ""})
        setOldPass({...oldPass, error: ""})
        if (oldPass.value === ""){
            setOldPass({...oldPass, error: "Введите старый пароль"})
            return false
        }
        if (!PasswordValidator(pass, setPass)){
            return false
        }
        if (repeatPass.value !== pass.value){
            setRepeatPass({...repeatPass, error: "Пароли должны совпадать"})
            return false
        }
    }

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Сменить пароль
            </Title>
            <Input value={oldPass.value} onChange={(e) => setOldPass({error: "", value: e.target.value})} type={"password"} placeholder={"Old password"}/>
            <Input value={pass.value} onChange={(e) => setPass({error: "", value: e.target.value})} type={"password"} placeholder={"New password"}/>
            <Input value={repeatPass.value} onChange={(e) => setRepeatPass({error: "", value: e.target.value})} type={"password"} placeholder={"Repeat password"}/>
            {oldPass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{oldPass.error}</div>}
            {pass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{pass.error}</div>}
            {repeatPass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{repeatPass.error}</div>}
            <Button onClick={handleClick}>
                Изменить пароль
            </Button>
        </Box>
    )
}

export default ChangePassword
