import {Box, Button, Input, Title} from "../ui";
import {useEffect, useState} from "react";

const useValidation = (value, validations) => {
    const [isEmpty, setEmpty] = useState(true)
    const [minLengthError, setMinLengthError] = useState(false)

    useEffect(() => {
        for (const validation in validations) {
            switch (validation) {
                case "minLength":
                    value.length < validations[validation] ? setMinLengthError(true) : setMinLengthError(false)
                    break;
                case "isEmpty":
                    value ? setEmpty(false) : setEmpty(true)
                    break;
                case "default":
                    break;
            }
        }
    }, [value])

    return {isEmpty, minLengthError}
}

const useInput = (initialValue, validations) => {
    const [value, setValue] = useState(initialValue)
    const [isDirty, setDirty] = useState(false)
    const valid = useValidation(value, validations)

    const onChange = (e) => {
        setValue(e.target.value)
    }

    const onBlur = (e) => {
        setDirty(true)
    }

    return {
        value, onChange, onBlur, isDirty, ...valid
    }
}

const Login = () => {
    const email = useInput("", {isEmpty: true, minLength: 3})
    const password = useInput("", {isEmpty: true, minLength: 5})

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            <form>
                <Input onChange={(e) => email.onChange(e)} onBlur={(e) => email.onBlur(e)} value={email.value}
                       name="email" type="email" placeholder="Email"/>
                {(email.isEmpty && email.isDirty) && <div style={{color: "red"}}>Поле не может быть пустым</div>}
                {(email.isEmpty && email.minLengthError) && <div style={{color: "red"}}>Некорректная длина</div>}
                <Input onChange={(e) => password.onChange(e)} onBlur={(e) => password.onBlur(e)} value={password.value}
                       type="password" placeholder="Password"/>
                <Button type="submit">
                    Войти
                </Button>
            </form>
        </Box>
    )
}

export default Login;
