const Login = () => {
    /* const email = useInput("", {isEmpty: true, minLength: 3})
     const password = useInput("", {isEmpty: true, minLength: 5})*/

    const [email, setEmail] = useState({
        value: "",
        error: ""
    })

    const [pass, setPass] = useState({
        value: "",
        error: ""
    })

    const handleClick = () => {
        if (email.value === ""){
            setEmail({...email, error: "Email обязателен к заполнению"})
            return false
        }
        else if (!String(email.value).toLowerCase().match(
            /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/
        )) {
            setEmail({...email, error: "Некорректный email"})
            return false
        }
    }

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            {/*<form>
                <Input onChange={(e) => email.onChange(e)} onBlur={(e) => email.onBlur(e)} value={email.value}
                       name="email" type="email" placeholder="Email"/>
                {(email.isEmpty && email.isDirty) && <div style={{color: "red"}}>Поле не может быть пустым</div>}
                {(email.isDirty && email.minLengthError) && <div style={{color: "red"}}>Некорректная длина</div>}
                <Input onChange={(e) => password.onChange(e)} onBlur={(e) => password.onBlur(e)} value={password.value}
                       type="password" placeholder="Password"/>
                <Button type="submit">
                    Войти
                </Button>
            </form>*/}
            <Input value={email.value} onChange={(e) => setEmail({error: "", value: e.target.value})} name="email" type="email"
                   placeholder="Email"/>
            <Input value={pass.value} onChange={(e) => setPass({error: "", value: e.target.value})} type="password" placeholder="Password"/>
            {email.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{email.error}</div>}
            <Button onClick={handleClick}>
                Войти
            </Button>
        </Box>
    )
}

export default Login;
