import {Box, Button, Input, Title} from "../ui";
import {useState} from "react";
import EmailValidator from "../validators/email-validator";

const Login = () => {

    const [email, setEmail] = useState({
        value: "",
        error: ""
    })

    const [pass, setPass] = useState({
        value: "",
        error: ""
    })

    const handleClick = () => {
        if (EmailValidator(email, ))
        if (email.value === "") {
            setEmail({...email, error: "Email обязателен к заполнению"})
            return false
        } else if (!String(email.value).toLowerCase().match(
            /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/
        )) {
            setEmail({...email, error: "Некорректный email"})
            return false
        } else if (pass.value === "") {
            setPass({...pass, error: "Пароль обязателен к заполнению"})
            return false
        } else if (!String(pass.value).match(
            /^(?=.*[A-Z]).{4,10}$/
        )) {
            setPass({...pass, error: "Как минимум 1 заглавная буква, от 4 до 10 символов"})
            return false
        }
    }

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            <Input value={email.value} onChange={(e) => setEmail({error: "", value: e.target.value})} name="email" type="email"
                   placeholder="Email"/>
            <Input value={pass.value} onChange={(e) => setPass({error: "", value: e.target.value})} type="password" placeholder="Password"/>
            {email.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{email.error}</div>}
            {pass.error && <div style={{position: "absolute", bottom: 80, left: 0, right: 0, color: "red"}}>{pass.error}</div>}
            <Button onClick={handleClick}>
                Войти
            </Button>
        </Box>
    )
}

export default Login;
