import {Box, Button, Input, Title} from "../ui";
import {useState} from "react";

const useInput = (initialValue) => {
    const [value, setValue] = useState(initialValue)
    const [isDirty, setDirty] = useState(false)

    const onChange = (e) => {
        setValue(e.target.value)
    }

    const onBlur = (e) => {
        setDirty(true)
    }

    return {
        value, onChange, onBlur
    }
}

const Login = () => {
    const email = useInput("")
    const password = useInput("")

    return (
        <Box style={{margin: "auto auto"}}>
            <Title>
                Авторизация
            </Title>
            <Input onChange={(e) => email.onChange(e)} onBlur={(e) => email.onBlur(e)} value={email.value} type={"email"} placeholder={"Email"}/>
            <Input onChange={(e) => password.onChange(e)} onBlur={(e) => password.onBlur(e)} value={password.value} type={"password"} placeholder={"Password"}/>
            <Button>
                Войти
            </Button>
        </Box>
    )
}

export default Login
